<script>
  /**
   * Set the src attribute of the img element
   * @type {string} [imgUrl = ""]
   */
  export let imgUrl = "";

  /**
   * Set the alt attribute of the img element
   * @type {string} [imgAlt = "Asset image"]
   */
  export let imgAlt = "Image";

  /**
   * @type {"foreground" | "background"} [variants = "foreground"]
   * **/
  export let variants = "foreground";
</script>

{#if variants == 'background'}
  <div class={$$restProps.class} style="background-image: url({imgUrl})" aria-label={imgAlt} />
{:else if variants == 'foreground'}<img class={$$restProps.class} alt={imgAlt} src={imgUrl} />{/if}
