<script>
  /**
   * Preconditions:variants => "image"
   * Set the src attribute of the img element
   * @type {string} [imgUrl = ""]
   */
  export let imgUrl = "";

  /**
   * Preconditions:variants => "image"
   * Set the alt attribute of the img element
   * @type {string} [imgAlt = "Avatar image"]
   */
  export let imgAlt = "Avatar image";

  /**
   * Set to `true` to disable the avatar
   * @type {boolean}[disabled=false]
   */
  export let disabled = false;
</script>

<img class="spectrum-Avatar {$$restProps.class}" class:is-disabled={disabled} src={imgUrl} alt={imgAlt} />
