<script>
  import { getContext } from "svelte";
  import CalendarBodyDay from "./CalendarBodyDay.svelte";

  export let week = [];

  let rubusCalendar = getContext("RubusCalendarData");
  function filter(arr, day) {
    let thisDate = new Date(day).getTime();
    if (arr.length === 2 && new Date(arr[0]).getTime() < new Date(arr[1]).getTime()) {
      if (thisDate >= new Date(arr[0]).getTime() && thisDate <= new Date(arr[1]).getTime()) {
        return true;
      }
    }
    for (let index = 0; index < arr.length; index++) {
      if (new Date(arr[index]).getTime() == thisDate) {
        return true;
      }
    }
  }
  function filterValidRange(arr, day) {
    if ($rubusCalendar.validRange == "any") {
      return false;
    }
    let thisDate = new Date(day).getTime();
    if (arr.length === 2 && new Date(arr[0]).getTime() < new Date(arr[1]).getTime()) {
      if (thisDate >= new Date(arr[0]).getTime() && thisDate <= new Date(arr[1]).getTime()) {
        return false;
      } else {
        return true;
      }
    }
  }
</script>

<tr role="row">
  {#each week as item}
    <CalendarBodyDay
      day={item}
      isFocused={filter($rubusCalendar.focused, item)}
      disabled={filter($rubusCalendar.disabled, item)}
      invalid={filter($rubusCalendar.validRange, item)} />
  {/each}
</tr>
