<script>
  import IllustratedError from "./IllustratedError.svelte";
  import IllustratedNotFound from "./IllustratedNotFound.svelte";
  import IllustratedTimeout from "./IllustratedTimeout.svelte";
  import IllustratedUnauthorized from "./IllustratedUnauthorized.svelte";
  import IllustratedUnavailable from "./IllustratedUnavailable.svelte";
  import IllustratedUpload from "./IllustratedUpload.svelte";
  /**
   * Specify the variants of illustrated message
   * @type {"error" | "notFound" | "timeout" |"unauthorized" | "unavailable" | "upload"} [variants="error"]
   */
  export let variants = "error";

  /**
   * Specify the title of illustrated message
   * @type {string} [title = ""]
   */
  export let title = "";

  /**
   * Used for instruction/call to action.
   * @type {boolean} [isCta = false]
   */
  export let isCta = false;
</script>

<div class="spectrum-IllustratedMessage" class:spectrum-IllustratedMessage--cta={isCta}>
  {#if variants === 'error'}
    <IllustratedError class="spectrum-IllustratedMessage-illustration" />
  {:else if variants === 'notFound'}
    <IllustratedNotFound class="spectrum-IllustratedMessage-illustration" />
  {:else if variants === 'timeout'}
    <IllustratedTimeout class="spectrum-IllustratedMessage-illustration" />
  {:else if variants === 'unauthorized'}
    <IllustratedUnauthorized class="spectrum-IllustratedMessage-illustration" />
  {:else if variants === 'unavailable'}
    <IllustratedUnavailable class="spectrum-IllustratedMessage-illustration" />
  {:else if variants === 'upload'}
    <IllustratedUpload class="spectrum-IllustratedMessage-illustration" />
  {/if}

  <h2 class="spectrum-Heading--pageTitle spectrum-IllustratedMessage-heading">{title}</h2>
  <p class="spectrum-Body--secondary spectrum-IllustratedMessage-description">
    <slot />
  </p>
</div>
