<script>
  import { afterUpdate } from "svelte";
  import { integrateAutoProps } from "../utils/integrate-style-props.js";

  /**
   * Overrides the style.
   * @type { IViewStyleProps } [style = {}]
   */
  export let style = {};

  let styleCss = "";
  afterUpdate(() => {
    styleCss = integrateAutoProps(style);
  });
</script>

<div style={styleCss} />
