<script>
  import { current_component } from "svelte/internal";
  import { getEventsAction } from "../utils/get-events-action.js";

  /**
   * Set the `href` to use an anchor link
   * @type {string}[href = ""]
   */
  export let href = "#";
  /**
   * Where to display the linked URL
   * @type {"_self" | "_blank" | "_parent" | "_top"}[target = ""]
   */
  export let target = "";

  /**
   * Specify the variants of link
   * @type {"primary" | "secondary" | "overBackground" } [variants="primary"]
   */
  export let variants = "primary";

  /**
   * The relationship of the linked URL as space-separated link types.
   *More: https://developer.mozilla.org/zh-CN/docs/Web/HTML/Link_types
   * @type {string}[rel = ""]
   */
  export let rel = "";

  /**
   * Specify the id of link
   * @type {string} [id = ""]
   */
  export let id = "";

  /**
   * Specify quiet mode id of link
   * @type {boolean} [isQuiet = false]
   */
  export let isQuiet = false;

  /**
   * Set to `true` to disable the link
   * @type {boolean}[disabled=false]
   */
  export let disabled = false;

  const eventsListen = getEventsAction(current_component);
  $: linkProps = {
    id,
    rel,
    disabled,
    ...$$restProps,
  };
</script>

<style global>
  .spectrum-Link--disabled {
    pointer-events: none;
    color: var(--spectrum-alias-text-color-disabled);
  }
  .spectrum-Link--overBackground.spectrum-Link--disabled {
    color: rgba(255, 255, 255, 0.6);
  }
</style>

<a
  {href}
  {target}
  {...linkProps}
  class="spectrum-Link spectrum-Link--{variants}"
  class:spectrum-Link--quiet={isQuiet}
  class:spectrum-Link--disabled={disabled}
  use:eventsListen><slot /></a>
