<script>
  /**
   * The element's unique identifier.
   * @type { string }[id = `radio-${Math.round(Math.random() * (99999 - 0)) + 0}`]
   */
  export let id = `radio-${Math.round(Math.random() * (99999 - 0)) + 0}`;

  /**
   * Set to `true` to checked the radio
   * @type {boolean}[checked=false]
   */
  export let checked = false;

  /**
   * Set to `true` to invalid the radio
   * @type {boolean}[checked=false]
   */
  export let isInvalid = false;

  /**
   * Set to `true` to disable the radio
   * @type {boolean}[disabled=false]
   */
  export let disabled = false;

  /**
   * Specify the quiet mode of radio
   * @type { boolean } [isQuiet= false]
   */
  export let isQuiet = false;

  /**
   * The name of the radio button, used when submitting an HTML form.
   * @type { string }[ name = "radio" ]
   */
  export let name = "radio";

  /**
   * The value of the radio button, used when submitting an HTML form.
   * @type { string }[value = ""]
   */
  export let value = "";

  /**
   * Specify the readonly mode of radio
   * @type {boolean}[readonly=false]
   */
  export let readonly = false;
</script>

<style global>
  .spectrum-Radio.is-readonly {
    pointer-events: none;
  }
</style>

<div
  class="spectrum-Radio"
  class:spectrum-Radio--quiet={isQuiet}
  class:is-invalid={isInvalid}
  class:is-readonly={readonly}>
  <input type="radio" {name} class="spectrum-Radio-input" {id} {checked} {disabled} {value} {readonly} />
  <span class="spectrum-Radio-button" />
  <label class="spectrum-Radio-label" for={id}><slot>{value}</slot></label>
</div>
