<script>
  /**
   * Specify the  multiLevel mode  of SideNav
   * @type { boolean }[multiLevel = false]
   */
  export let multiLevel = false;

  /**
   * Specify the heading title of SideNav
   * @type { string }[ title = "" ]
   */
  export let title = "";

  /**
   * The element's unique identifier.
   * @type { string }[ id = `nav-heading-${Math.round(Math.random() * (99999 - 0)) + 0} ]
   */
  export let id = `nav-heading-${Math.round(Math.random() * (99999 - 0)) + 0}`;
</script>

{#if title}
  <h2 class="spectrum-SideNav-heading" {id}>{title}</h2>
{/if}

<ul class="spectrum-SideNav" class:spectrum-SideNav--multiLevel={multiLevel} aria-labelledby={title && id}>
  <slot />
</ul>
