<script>
  /**
   * Specify whether the switch is checked
   * @type {boolean} [checked=false]
   */
  export let checked = false;

  /**
   * Set to `true` to disable the switch
   * @type {boolean} [disabled=false]
   */
  export let disabled = false;

  /**
   * Set an id for the input element
   * @type {string} [id = "switch-" + Math.random().toString(24)]
   */
  export let id = "switch-" + Math.random().toString(24);

  /**
   * Specify whether the switch is emphasized
   * @type {boolean} [emphasized=false]
   */
  export let emphasized = false;

  /**
   * Set to `true` for the switch to be read-only
   * @type {boolean} [readonly=false]
   */
  export let readonly = false;

  /**
   * Set the label text of the Switch
   * @type { string } [label = "Switch"]
   */
  export let label = "Switch";
</script>

<style global>
  .spectrum-Switch.is-readonly {
    pointer-events: none;
  }
</style>

<div
  class="spectrum-Switch"
  class:spectrum-Switch--emphasized={emphasized}
  class:is-readonly={readonly}
  on:click
  on:keydown
  on:keyup
  on:keypress
  on:dblclick>
  <input type="checkbox" class="spectrum-Switch-input" {id} bind:checked {disabled} {readonly} />
  <span class="spectrum-Switch-switch" />
  <label class="spectrum-Switch-label" for={id}><slot>{label}</slot></label>
</div>
