<script>
  /**
   * Set the src attribute of the img element
   * @type {string} [imgUrl = ""]
   */
  export let imgUrl = "";

  /**
   * Set the alt attribute of the img element
   * @type {string} [imgAlt = "Asset image"]
   */
  export let imgAlt = "Image";

  /**
   * Use foreground or background to display the image
   * @type {"foreground" | "background"} [variants = "foreground"]
   */
  export let variants = "foreground";

  /**
   * Set the background-color style of the element
   * @type {string} [backgroundColor = ""]
   */
  export let backgroundColor = "";

  /**
   * Whether the Thumbnail is selected
   * @type {boolean} [isSelected = false]
   */
  export let isSelected = false;

  /**
   * Whether the Thumbnail is focused
   * @type {boolean} [isFocused = false]
   */
  export let isFocused = false;

  /**
   * Set Thumbnail scale specifications
   * @type {"S" | "M" | "L" | "XL" | "XXL"} [scale = "M"]
   */
  export let scale = "M";
</script>

<div
  class="spectrum-Thumbnail spectrum-Thumbnail--{scale} {$$restProps.class}"
  class:is-selected={isSelected}
  class:is-focused={isFocused}>
  {#if variants === 'foreground'}
    {#if backgroundColor}
      <div class="spectrum-Thumbnail-background" style="background-color: {backgroundColor}" title={imgAlt} />
    {/if}
    <img class="spectrum-Thumbnail-image" src={imgUrl} alt={imgAlt} />
  {:else}
    <div class="spectrum-Thumbnail-background" style="background-image: url({imgUrl})" title={imgAlt} />
  {/if}
</div>
