<script>
  /**
   * Set Heading scale specifications
   * @type {"XXS" | "XS" | "S" | "M" | "L" | "XL" | "XXL" | "XXXL"} [scale = "M"]
   */
  export let scale = "M";

  /**
   * Whether to use serif font family in the context
   * @type {boolean} [isSerif = false]
   */
  export let isSerif = false;

  /**
   * Set the font weight
   * @type {"heavy" | "light" | "default" } [thickness = "default"]
   */
  export let thickness = "default";
</script>

<h1
  class="spectrum-Heading spectrum-Heading--{scale}  spectrum-Heading--{thickness}"
  class:spectrum-Heading--serif={isSerif}>
  <slot />
</h1>
