type alignSelfType =
  | "auto"
  | "normal"
  | "start"
  | "end"
  | "center"
  | "flex-start"
  | "flex-end"
  | "self-start"
  | "self-end"
  | "stretch";

type justifySelfType =
  | "auto"
  | "normal"
  | "start"
  | "end"
  | "flex-start"
  | "flex-end"
  | "self-start"
  | "self-end"
  | "center"
  | "left"
  | "right"
  | "stretch";

export interface ILayout {
  flex?: string | number | boolean;
  flexGrow?: number;
  flexShrink?: number;
  flexBasis?: number | string;
  alignSelf?: alignSelfType;
  justifySelf?: justifySelfType;
  order?: number;
  gridArea?: string;
  gridColumn?: string;
  gridRow?: string;
  gridColumnStart?: string;
  gridColumnEnd?: string;
  gridRowStart?: string;
  gridRowEnd?: string;
}
