<script>
  import { afterUpdate, onMount } from "svelte";
  export let scale = "M";
  export let className = "";
  export let width = "";
  export let height = "";
  export let ariaLabel = "CheckmarkMedium";
  let path;
  let sw;
  let sh;

  let flag = 14;
  onMount(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
  });

  afterUpdate(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
    if (!scale || scale == "M") {
      let rootClassName = document && document.documentElement.className;
      if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
        scale = "L";
      } else {
        scale = "M";
      }
    }
  });
</script>



<svg
  aria-label={ariaLabel}
  {...$$restProps}
  width={width || sw || flag}
  height={height || sh || flag}
  fill="currentColor"
  class="spectrum-Icon {className}">
  {#if scale === 'L'}
    <path
      bind:this={path}
      d="M6 14a1 1 0 01-.789-.385l-4-5a1 1 0 111.577-1.23L6 11.376l7.213-8.99a1 1 0 111.576 1.23l-8 10a1 1 0
      01-.789.384z" />
  {:else if scale === 'M'}
    <path
      bind:this={path}
      d="M4.5 10a1.022 1.022 0 01-.799-.384l-2.488-3a1 1 0 011.576-1.233L4.5 7.376l4.712-5.991a1 1 0 111.576 1.23l-5.51
      7A.978.978 0 014.5 10z" />
  {/if}
</svg>
