<script>
  import { afterUpdate, onMount } from "svelte";
  export let scale = "M";
  export let className = "";
  export let width = "";
  export let height = "";
  export let ariaLabel = "ChevronLeftMedium";
  let path;
  let sw;
  let sh;

  let flag = 14;
  onMount(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
  });

  afterUpdate(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
    if (!scale || scale == "M") {
      let rootClassName = document && document.documentElement.className;
      if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
        scale = "L";
      } else {
        scale = "M";
      }
    }
  });
</script>



<svg
  aria-label={ariaLabel}
  {...$$restProps}
  width={width || sw || flag}
  height={height || sh || flag}
  fill="currentColor"
  class="spectrum-Icon {className}">
  {#if scale === 'L'}
    <path
      bind:this={path}
    d="M7.197 10.283L2.914 6l4.283-4.283A1 1 0 105.783.303l-4.99 4.99a1 1 0 000 1.414l4.99 4.99a1 1 0 101.414-1.414z"
/>
  {:else if scale === 'M'}
    <path
      bind:this={path}
    d="M5.697 8.283L2.414 5l3.283-3.283A1 1 0 104.283.303l-3.98 3.99a1 1 0 000 1.414l3.98 3.99a1 1 0 101.414-1.414z"/>
  {/if}
</svg>
