<script>
  import { afterUpdate, onMount } from "svelte";
  export let scale = "M";
  export let className = "";
  export let width = "";
  export let height = "";
  export let ariaLabel = "ChevronRightLarge";
  let path;
  let sw;
  let sh;

  let flag = 14;
  onMount(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
  });

  afterUpdate(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
    if (!scale || scale == "M") {
      let rootClassName = document && document.documentElement.className;
      if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
        scale = "L";
      } else {
        scale = "M";
      }
    }
  });
</script>



<svg
  aria-label={ariaLabel}
  {...$$restProps}
  width={width || sw || flag}
  height={height || sh || flag}
  fill="currentColor"
  class="spectrum-Icon {className}">
  {#if scale === 'L'}
    <path
      bind:this={path}
    d="M15 10.004a1.243 1.243 0 00-.38-.894L5.631.364a1.249 1.249 0 10-1.741 1.79l8.066 7.85-8.069 7.847a1.249 1.249 0
    001.741 1.79l8.992-8.74a1.246 1.246 0 00.379-.897z"
/>
  {:else if scale === 'M'}
    <path
      bind:this={path}
    d="M11.5 8a1.241 1.241 0 00-.386-.897L4.128.36a1.248 1.248 0 10-1.733 1.797L8.45 8l-6.058 5.84a1.248 1.248 0 101.733
    1.797L11.117 8.9A1.245 1.245 0 0011.5 8z"/>
  {/if}
</svg>
