<script>
  import { afterUpdate, onMount } from "svelte";
  export let scale = "M";
  export let className = "";
  export let width = "";
  export let height = "";
  export let ariaLabel = "ChevronUpSmall";
  let path;
  let sw;
  let sh;

  let flag = 14;
  onMount(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
  });

  afterUpdate(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
    if (!scale || scale == "M") {
      let rootClassName = document && document.documentElement.className;
      if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
        scale = "L";
      } else {
        scale = "M";
      }
    }
  });
</script>



<svg
  aria-label={ariaLabel}
  {...$$restProps}
  width={width || sw || flag}
  height={height || sh || flag}
  fill="currentColor"
  class="spectrum-Icon {className}">
  {#if scale === 'L'}
    <path
      bind:this={path}
    d="M5 1a.747.747 0 00-.53.22L.23 5.46a.75.75 0 101.06 1.06L5 2.81l3.71 3.71a.75.75 0 101.06-1.06L5.53 1.22A.747.747
    0 005 1z"
/>
  {:else if scale === 'M'}
    <path
      bind:this={path}
    d="M4 .5a.747.747 0 00-.53.22C2.862 1.297 1.5 2.758.23 3.96a.75.75 0 101.06 1.06L4 2.31l2.71 2.71a.75.75 0
    101.06-1.06L4.53.72A.747.747 0 004 .5z"/>
  {/if}
</svg>
