  class="spectrum-Icon {className}">
  {#if scale === 'L'}
    <path
      bind:this={path}
    d="M15.697 14.283L9.414 8l6.283-6.283A1 1 0 1014.283.303L8 6.586 1.717.303A1 1 0 10.303 1.717L6.586 8 .303 14.283a1
    1 0 101.414 1.414L8 9.414l6.283 6.283a1 1 0 101.414-1.414z"
/>
  {:else if scale === 'M'}
