<script>
  import { afterUpdate, onMount } from "svelte";
  export let scale = "M";
  export let className = "";
  export let width = "";
  export let height = "";
  export let ariaLabel = "More";
  let path;
  let sw;
  let sh;

  let flag = 14;
  onMount(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
  });

  afterUpdate(() => {
    if (path) {
      sw = path.getBoundingClientRect().width;
      sh = path.getBoundingClientRect().height;
    }
    if (!scale || scale == "M") {
      let rootClassName = document && document.documentElement.className;
      if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
        scale = "L";
      } else {
        scale = "M";
      }
    }
  });
</script>



<svg
  aria-label={ariaLabel}
  {...$$restProps}
  width={width || sw || flag}
  height={height || sh || flag}
  fill="currentColor"
  class="spectrum-Icon {className}">
  {#if scale === 'L'}
    <path
      bind:this={path}
    d="M11 8.95A2.05 2.05 0 118.95 11 2.05 2.05 0 0111 8.95zm6 0A2.05 2.05 0 1114.95 11 2.05 2.05 0 0117 8.95zm-12
    0A2.05 2.05 0 112.95 11 2.05 2.05 0 015 8.95z"
/>
  {:else if scale === 'M'}
    <path
      bind:this={path}
    d="M9 7.1A1.9 1.9 0 117.1 9 1.9 1.9 0 019 7.1zm6 0A1.9 1.9 0 1113.1 9 1.9 1.9 0 0115 7.1zm-12 0A1.9 1.9 0 111.1 9
    1.9 1.9 0 013 7.1z"/>
  {/if}
</svg>
