<script>
  import { afterUpdate } from "svelte";
  import { viewBoxes } from "./util.js";
   export let scale = "M";  
  export let width = "100%";
  export let height = "100%";
  export let className = "";
  
  export let ariaLabel = "DragHandle";
  let autoScale = 18;
  afterUpdate(() => {
    let rootClassName = document && document.documentElement.className;
    if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
      autoScale = 22;
    }
  });
</script>

<svg
  viewBox={viewBoxes[scale]}
  aria-label={ariaLabel}
  {...$$restProps}
  width={width !== '100%' ? width : autoScale}
  height={height !== '100%' ? height : autoScale}
  fill="currentColor"
  class="spectrum-Icon spectrum-Icon-workflow {className}">
  <rect height={2} rx={0.75} ry={0.75} width={2} x={12} y={4} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={12} y={10} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={12} y={16} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={12} y={22} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={12} y={28} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={18} y={4} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={18} y={10} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={18} y={16} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={18} y={22} />
  <rect height={2} rx={0.75} ry={0.75} width={2} x={18} y={28} />
</svg>
