<script>
  import { afterUpdate } from "svelte";
  import { viewBoxes } from "./util.js";
   export let scale = "M";  
  export let width = "100%";
  export let height = "100%";
  export let className = "";
  
  export let ariaLabel = "Histogram";
  let autoScale = 18;
  afterUpdate(() => {
    let rootClassName = document && document.documentElement.className;
    if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
      autoScale = 22;
    }
  });
</script>

<svg
  viewBox={viewBoxes[scale]}
  aria-label={ariaLabel}
  {...$$restProps}
  width={width !== '100%' ? width : autoScale}
  height={height !== '100%' ? height : autoScale}
  fill="currentColor"
  class="spectrum-Icon spectrum-Icon-workflow {className}">
  <rect height={10} rx={0.5} ry={0.5} width={2} x={2} y={24} />
  <rect height={18} rx={0.5} ry={0.5} width={2} x={6} y={16} />
  <rect height={18} rx={0.5} ry={0.5} width={2} x={18} y={16} />
  <rect height={14} rx={0.5} ry={0.5} width={2} x={26} y={20} />
  <rect height={6} rx={0.5} ry={0.5} width={2} x={30} y={28} />
  <rect height={28} rx={0.5} ry={0.5} width={2} x={10} y={6} />
  <rect height={22} rx={0.5} ry={0.5} width={2} x={14} y={12} />
  <rect height={24} rx={0.5} ry={0.5} width={2} x={22} y={10} />
</svg>
