<script>
  import { afterUpdate } from "svelte";
  import { viewBoxes } from "./util.js";
   export let scale = "M";  
  export let width = "100%";
  export let height = "100%";
  export let className = "";
  
  export let ariaLabel = "TextBulletedHierarchy";
  let autoScale = 18;
  afterUpdate(() => {
    let rootClassName = document && document.documentElement.className;
    if (rootClassName && rootClassName.indexOf("spectrum--large") != -1) {
      autoScale = 22;
    }
  });
</script>

<svg
  viewBox={viewBoxes[scale]}
  aria-label={ariaLabel}
  {...$$restProps}
  width={width !== '100%' ? width : autoScale}
  height={height !== '100%' ? height : autoScale}
  fill="currentColor"
  class="spectrum-Icon spectrum-Icon-workflow {className}">
  <rect height={6} rx={2.8} ry={2.8} width={6} x={4} y={2} />
  <rect height={6} rx={2.8} ry={2.8} width={6} x={12} y={14} />
  <rect height={6} rx={2.8} ry={2.8} width={6} x={12} y={26} />
  <rect height={4} rx={1} ry={1} width={14} x={20} y={28} />
  <rect height={4} rx={1} ry={1} width={14} x={20} y={16} />
  <rect height={4} rx={1} ry={1} width={22} x={12} y={4} />
</svg>
